const execution = $json.execution || {};
const workflow = $json.workflow || {};
const error = execution.error || {};

const name = workflow.name || 'Unknown Workflow';
const errorMsg = error.message || 'Unknown error';
const execUrl = execution.url || '';
const now = new Date().toLocaleString('en-CA', { timeZone: 'America/Toronto' });

const message = `üö® *WORKFLOW ERROR*\n\n‚öôÔ∏è *Workflow:* ${name}\n‚ùå *Error:* ${errorMsg}\nüïê *Time:* ${now}${execUrl ? '\n\nüîó [View Execution](' + execUrl + ')' : ''}`;

return { json: { telegramMessage: message } };
